import React, { useState } from "react";
import axios from "axios";

function Viewer() {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setResult(null);
    try {
      const response = await axios.post("http://localhost:8000/extract", { url });
      setResult(response.data);
    } catch (err) {
      setError("Extraction failed. Please check the URL and try again.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>URL Asset Extractor</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Enter URL"
          style={{ width: "300px", marginRight: "10px" }}
        />
        <button type="submit">Extract</button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {result && (
        <div style={{ marginTop: "20px" }}>
          {result?.profile_image_id && (
            <div>
              <h3>Profile Image:</h3>
              <img
                src={`http://localhost:8000/image/${result.profile_image_id}`}
                alt="Profile"
                width="300"
              />
            </div>
          )}
          {result?.banner_image_id && (
            <div>
              <h3>Banner Image:</h3>
              <img
                src={`http://localhost:8000/image/${result.banner_image_id}`}
                alt="Banner"
                width="300"
              />
            </div>
          )}
          {result?.text_file_id && (
            <div>
              <h3>Extracted Text:</h3>
              <a
                href={`http://localhost:8000/text/${result.text_file_id}`}
                target="_blank"
                rel="noreferrer"
              >
                View Text File
              </a>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Viewer;
