import React, { useState } from 'react';
import axios from 'axios';

const Viewer = () => {
  const [url, setUrl] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    try {
      const response = await axios.post('http://localhost:8000/extract', { url });
      setResult(response.data);
      setError('');
    } catch (err) {
      console.error(err);
      setError('Error extracting assets.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>URL Asset Extractor</h1>
      <input
        type="text"
        placeholder="Enter URL"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        style={{ width: '60%', marginRight: '1rem' }}
      />
      <button onClick={handleSubmit}>Extract</button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {result && (
        <div style={{ marginTop: '2rem' }}>
          <div>
            <h3>Profile Image (fallback URL)</h3>
            {result.profile_file_id ? (
              <img
                src={`http://localhost:8000/image/${result.profile_file_id}`}
                alt="Profile"
                width="300"
              />
            ) : (
              result.profile_image && (
                <img src={result.profile_image} alt="Profile Fallback" width="300" />
              )
            )}
          </div>

          <div style={{ marginTop: '2rem' }}>
            <h3>Banner Image (from GridFS)</h3>
            {result.banner_file_id ? (
              <img
                src={`http://localhost:8000/image/${result.banner_file_id}`}
                alt="Banner"
                width="600"
              />
            ) : (
              result.banner_image && (
                <img src={result.banner_image} alt="Banner Fallback" width="600" />
              )
            )}
          </div>

          <div style={{ marginTop: '2rem' }}>
            <h3>Extracted Text:</h3>
            {result.text_file_id ? (
              <iframe
                src={`http://localhost:8000/text/${result.text_file_id}`}
                width="100%"
                height="300"
                title="Text Content"
              />
            ) : (
              <pre>{JSON.stringify(result.text_preview || result.error, null, 2)}</pre>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Viewer;
