import React, { useState } from 'react';
import axios from 'axios';

const Viewer = () => {
  const [url, setUrl] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    try {
      const response = await axios.post('http://localhost:8000/extract', { url });
      setResult(response.data);
      setError('');
    } catch (err) {
      console.error(err);
      setError('Error extracting assets.');
    }
  };

  const renderImage = (label, fileId, fallbackUrl) => {
    if (fileId && fileId !== 'None') {
      return (
        <div>
          <h3>{label} (from GridFS)</h3>
          <img src={`http://localhost:8000/image/${fileId}`} alt={label} width="300" />
        </div>
      );
    } else if (fallbackUrl) {
      return (
        <div>
          <h3>{label} (fallback URL)</h3>
          <img src={fallbackUrl} alt={label} width="300" />
        </div>
      );
    }
    return null;
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>URL Asset Extractor</h1>
      <input
        type="text"
        placeholder="Enter URL"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        style={{ width: '60%', marginRight: '1rem' }}
      />
      <button onClick={handleSubmit}>Extract</button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {result && (
        <>
          {renderImage('Profile Image', result.profile_file_id, result.profile_image)}
          {renderImage('Banner Image', result.banner_file_id, result.banner_image)}

          {result.text_file_id && (
            <div>
              <h3>Extracted Text:</h3>
              <iframe
                src={`http://localhost:8000/text/${result.text_file_id}`}
                width="100%"
                height="400px"
                title="Text Content"
              />
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Viewer;
